mapper.insert:
insert into ts_major (major_id, name, dept_id
  )
values (#{majorId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{deptId,jdbcType=VARCHAR}
  )

mapper.update:
update ts_major
set name = #{name,jdbcType=VARCHAR},
  dept_id = #{deptId,jdbcType=VARCHAR}
where major_id = #{majorId,jdbcType=VARCHAR}

mapper.select:
select major_id, name, dept_id
from ts_major
where major_id = #{majorId,jdbcType=VARCHAR}
