mapper.delete:
delete from ts_student_course
where sno = #{sno,jdbcType=VARCHAR}
  and tc_id = #{tcId,jdbcType=VARCHAR}

mapper.update:
update ts_student_course
set score = #{score,jdbcType=INTEGER},
  appraise = #{appraise,jdbcType=LONGVARCHAR}
where sno = #{sno,jdbcType=VARCHAR}
  and tc_id = #{tcId,jdbcType=VARCHAR}

mapper.select:
select sno, tc_id, score, appraise
from ts_student_course
where sno = #{sno,jdbcType=VARCHAR}
  and tc_id = #{tcId,jdbcType=VARCHAR}
