mapper.update:
update ts_target
set content = #{content,jdbcType=VARCHAR},
  weight = #{weight,jdbcType=INTEGER},
  active = #{active,jdbcType=VARCHAR}
where target_id = #{targetId,jdbcType=VARCHAR}

mapper.select:
select target_id, content, weight, active
from ts_target
where target_id = #{targetId,jdbcType=VARCHAR}
