mapper.update:
update ts_teacher_course
set cno = #{cno,jdbcType=INTEGER},
  teacher_no = #{teacherNo,jdbcType=INTEGER},
  term_no = #{termNo,jdbcType=SMALLINT},
  sum = #{sum,jdbcType=INTEGER}
where tc_id = #{tcId,jdbcType=VARCHAR}

mapper.select:
select tc_id, cno, teacher_no, term_no, sum
from ts_teacher_course
where tc_id = #{tcId,jdbcType=VARCHAR}
