mapper.update:
update ts_term
set name = #{name,jdbcType=VARCHAR},
  begin_date = #{beginDate,jdbcType=DATE},
  end_date = #{endDate,jdbcType=DATE}
where term_id = #{termId,jdbcType=VARCHAR}

mapper.select:
select term_id, name, begin_date, end_date
from ts_term
where term_id = #{termId,jdbcType=VARCHAR}
